import Link from "next/link";
import theme from "../styles/theme";

export default function ProductList(props) {
  const rows = [];
  let row = [];
  props.items.forEach((item) => {
    if ((row.length = props.columns)) {
      rows.push(row);
      row = [];
    }
    row.push(item);
  });
  if (row.length > 0) {
    rows.push(row);
  }
  return (
    <React.Fragment>
      <h1
        style={{
          textAlign: "center",
          fontFamily: theme.fonts.sectionTitle,
          fontWeight: theme.fonts.sectionWeight,
        }}
      >
        {props.title}
      </h1>
      {rows.map((row) => {
        return null;
      })}
      <div className="rootWrapper">
        <div className="sectionsWrapper">
          {props.items.map((item, index) => {
            return (
              <Link href={item.href} key={index}>
                <a className="item">
                  <img src={item.img} alt={item.imgAlt} />
                  <h3 className="link">{item.title}</h3>
                </a>
              </Link>
            );
          })}
        </div>

        <style jsx>{`
          .rootWrapper {
            display: flex;
            align-items: center;
            justify-content: space-around;
            margin: 0 -5px;
          }

          .sectionsWrapper {
            display: flex;
            align-items: center;
            justify-content: center;
            flex-wrap: wrap;
            text-align: center;
          }

          .item {
            text-decoration: none;
            color: ${theme.colors.productTitle};
          }

          @media screen and (max-width: 768px) {
            .item {
              width: 100%;
            }
          }

          @media screen and (min-width: 768px) and (max-width: 1240px) {
            .item {
              width: 50%;
            }
          }

          @media screen and (min-width: 1240px) {
            .item {
              width: 24%;
              margin: 0 5px;
            }
          }

          .link {
            text-align: center;
            font-family: ${theme.fonts.link};
            font-weight: ${theme.fonts.productTitleWeight};
            font-size: ${theme.fonts.productTitleSize};
          }
        `}</style>
      </div>
    </React.Fragment>
  );
}
