import Link from "next/link";

export default function SeasonsFlowers() {
  return (
    <React.Fragment>
      <h1
        style={{
          textAlign: "center",
          fontFamily: "Montserrat Medium,Montserrat,Verdana,sans-serif",
          fontWeight: 500,
        }}
      >
        Shop All Spring Flowers
      </h1>

      <div className="sectionsWrapper">
        <Link href="/sale">
          <a className="item">
            <img src="/sale.webp" alt="sale" />
            <h2 className="link">Sale</h2>
          </a>
        </Link>
        <Link href="/sympathy">
          <a className="item">
            <img src="/sympathy.webp" alt="sympathy" />
            <h2 className="link">Sympathy</h2>
          </a>
        </Link>
        <Link href="/gifts">
          <a className="item">
            <img src="/gifts.jpg" alt="gifts" />
            <h2 className="link">Gifts</h2>
          </a>
        </Link>
        <Link href="/plants">
          <a className="item">
            <img src="/plants.jpg" alt="plants" />
            <h2 className="link">Plants</h2>
          </a>
        </Link>
        <Link href="/birthday">
          <a className="item">
            <img src="/birthday.webp" alt="birthday" />
            <h2 className="link">Birthday</h2>
          </a>
        </Link>
        <Link href="/samedaydelivery">
          <a className="item">
            <img src="/sddelivery.webp" alt="Same Day Delivery" />
            <h2 className="link">Same Day Delivery</h2>
          </a>
        </Link>
        <style jsx>{`
          .sectionsWrapper {
            display: flex;
            align-items: center;
            flex-wrap: wrap;
            text-align: center;
            font-family: Montserrat Bold, Montserrat, Verdana, sans-serif;
            font-weight: 400;
          }

          @media screen and (max-width: 1000px) and (orientation: landscape),
            screen and (max-height: 1000px) and (orientation: portrait) {
            .item {
              width: 50%;
              text-decoration: none;
              color: #8e7329;
            }
          }

          @media screen and (min-width: 1000px) and (max-width: 1280px) and (orientation: landscape),
            screen and (min-height: 1000px) and (max-height: 1280px) and (orientation: portrait) {
            .item {
              width: 33.33%;
              text-decoration: none;
              color: #8e7329;
            }
          }

          @media screen and (min-width: 1280px) and (orientation: landscape),
            screen and (min-height: 1280px) and (orientation: portrait) {
            .item {
              flex-grow: 1;
              text-decoration: none;
              color: #8e7329;
            }
          }

          .item:hover {
            text-decoration: underline;

            color: #755e1e;
          }

          .link {
            text-align: center;
          }
        `}</style>
      </div>
    </React.Fragment>
  );
}
