import Link from "next/link";

export default function SeasonsFlowers(props) {
  return (
    <React.Fragment>
      <h1
        style={{
          textAlign: "center",
          fontFamily: "Montserrat Medium,Montserrat,Verdana,sans-serif",
          fontWeight: 500,
        }}
      >
        {props.title}
      </h1>

      <div className="rootWrapper">
        <div className="sectionsWrapper">
          {props.items.map((item) => {
            <Link href={item.href}>
              <a className="item">
                <img src={item.img} alt={item.imgAlt} />
                <h2 className="link">{item.title}</h2>
              </a>
            </Link>;
          })}
        </div>

        <style jsx>{`
          .rootWrapper {
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .sectionsWrapper {
            display: flex;
            align-items: center;
            justify-content: center;
            flex-wrap: wrap;
            text-align: center;
            font-family: Montserrat Bold, Montserrat, Verdana, sans-serif;
            font-weight: 400;
          }

          @media screen and (max-width: 1000px) and (orientation: landscape),
            screen and (max-height: 1000px) and (orientation: portrait) {
            .item {
              width: 50%;
              text-decoration: none;
              color: #8e7329;
            }
          }

          @media screen and (min-width: 1000px) and (max-width: 1280px) and (orientation: landscape),
            screen and (min-height: 1000px) and (max-height: 1280px) and (orientation: portrait) {
            .item {
              width: 33.33%;
              text-decoration: none;
              color: #8e7329;
            }
          }

          @media screen and (min-width: 1280px) and (orientation: landscape),
            screen and (min-height: 1280px) and (orientation: portrait) {
            .item {
              flex-grow: 1;
              text-decoration: none;
              color: #8e7329;
            }
          }

          .item:hover {
            text-decoration: underline;

            color: #755e1e;
          }

          .link {
            text-align: center;
          }
        `}</style>
      </div>
    </React.Fragment>
  );
}
