const theme = {
  fonts: {
    sectionTitle: "Montserrat Medium,Montserrat,Verdana,sans-serif",
    sectionWeight: "500",
    productTitleWeight: "300",
    link: "Montserrat Bold, Montserrat, Verdana, sans-serif",
    linkWeight: 400,
    productNameWeight: 400,
    productNameSize: "14pt",
  },

  colors: {
    link: "#8e7329",
    productTitle: "black",
    linkHover: "#755e1e",
  },
};

export default theme;
