const theme = {
  fonts: {
    sectionTitle: "Montserrat Medium,Montserrat,Verdana,sans-serif",
    sectionWeight: "500",
    link: "Montserrat Bold, Montserrat, Verdana, sans-serif",
    linkWeight: 400,
    productTitleWeight: 400,
    productTitleSize: "15pt",
  },

  colors: {
    link: "#8e7329",
    productTitle: "black",
    linkHover: "#755e1e",
  },
};

export default theme;
